mapper.insert:

insert into neuedu_product (id, category_id, name, 
  subtitle, main_image, price, 
  stock, status, create_time, 
  update_time, sub_images, detail
  )
values (#{id,jdbcType=INTEGER}, #{categoryId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{subtitle,jdbcType=VARCHAR}, #{mainImage,jdbcType=VARCHAR}, #{price,jdbcType=DECIMAL}, 
  #{stock,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{subImages,jdbcType=LONGVARCHAR}, #{detail,jdbcType=LONGVARCHAR}
  )

mapper.select:
select id, category_id, name, subtitle, main_image, price, status from neuedu_product
name like #{keyword} and is_new=0 and is_banner=0 and is_hot=0 and category_id in
#{data_i}


select id, category_id, name, subtitle, main_image, price, status
from neuedu_product
where category_id=#{categoryId} and is_new=0 and is_banner=0 and is_hot=0 and name like #{keyword}

select id, category_id, name, subtitle, main_image, price, status from neuedu_product
is_new=0 and is_banner=0 and is_hot=0 and category_id in
#{data_i}


select id, category_id, name, subtitle, main_image, price, stock, status, is_new,is_hot,is_banner,create_time,
update_time, sub_images, detail
from neuedu_product where is_new=#{is_new} and is_hot=#{is_hot} and is_banner=#{is_banner}
